pragma solidity ^0.6.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/GSN/Context.sol";

/**
 * @dev {ERC20} token, including:
 *
 *  - ability for holders to burn (destroy) their tokens
 *  - a minter role that allows for token minting (creation)
 *
 * This contract uses {AccessControl} to lock permissioned functions using the
 * different roles - head to its documentation for details.
 *
 * The account that deploys the contract will be granted the minter and pauser
 * roles, as well as the default admin role, which will let it grant both minter
 * and pauser roles to other accounts.
 */
contract RIOToken is Context, AccessControl, ERC20Burnable {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bool private _isMintLocked = false;

    /**
     * @dev Grants `DEFAULT_ADMIN_ROLE`, and `MINTER_ROLE` to the
     * account that deploys the contract.
     *
     * See {ERC20-constructor}.
     */
    constructor() public ERC20("Realio Network", "RIO") {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());

        _setupRole(MINTER_ROLE, _msgSender());

        mint(msg.sender, 100000000000000000000000000);

        _lockMinting();
    }

    /**
     * @dev Creates `amount` new tokens for `to`.
     *
     * See {ERC20-_mint}.
     *
     * Requirements:
     *
     * - the caller must have the `MINTER_ROLE`.
     * - mint can only be called at contract creation time
     */
    function mint(address to, uint256 amount) public virtual {
        require(hasRole(MINTER_ROLE, _msgSender()), "RIOToken: must have minter role to mint");
        require(!getIsMintLocked(), "RIOToken: mint is locked");
        _mint(to, amount);
    }

    /**
    * @dev Set minting lock
    *
    * Requirements:
    *
    * - minting must not be locked
    *
    * Locks any minting to occur after contract creation time
    */
    function _lockMinting() internal {
        require(!getIsMintLocked(), "RIOToken: mint is locked");
        _isMintLocked = true;
    }

    /**
    * @dev Get minting lock value
    *
    * Returns the mint lock status
    */
    function getIsMintLocked() public view returns (bool isMintLocked) {
        return _isMintLocked;
    }
}

